import {useState} from 'react'
import './Login.css'

const Login = (props) => {
    const [email, setEmail]=useState('')
    const [pass, setPass]=useState('')

    const handleSubmit =(e) =>{
        e.preventDefault()
        console.log(e);
    }
  return (
    <div className='auth-form-container'>

        <form className='login-form' onSubmit={handleSubmit}>
            <label className='label-form' htmlfor="email">Email</label>
            <input className='input-form' value={email} onChange={(e)=>setEmail(e.target.value)} type="email" placeholder='youremail@example.com' id='email' name='email'/>
            <label className='label-form' htmlfor="password">Password</label>
            <input  className='input-form' value={pass} onChange={(e)=>setPass(e.target.value)} type="password" placeholder='*******' id='password' name='password'/>
            <button className='btn-login'>Log In</button>
        </form>
        <button className='btn-link' onClick={()=>props.onFormSwitch('Register')}>Don't have an account? Register here</button>
    </div>
  )
}

export default Login
