import { useEffect, useState } from 'react';
import './SearchBar.css';
import SpotDataBase from '../../utilities/SpotDataBase';

const SearchBar = ({ onSearch }) => {
  const [searchInput, setSearchInput] = useState('');

  const handleInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  const handleSpotSelect = (spot) => {
    onSearch(spot.name);
    setSearchInput('');
  };

  const spots = SpotDataBase.filter((spot) =>
    spot.name.toLowerCase().includes(searchInput.toLowerCase())
  );

  return (
    <div className="searchbar">
      <input
        className="search-input"
        type="text"
        placeholder="Select your spot here!"
        value={searchInput}
        onChange={handleInputChange}
      />
      {searchInput !== '' && (
        <ul className="dropdown-search">
          {spots.map((spot) => (
            <li key={spot.id}>
              <p onClick={() => handleSpotSelect(spot)}>{spot.name}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
