import React from 'react'
import './ForecastCardExtended.css'
import HourlyForecast from './forecast-extended-components/HourlyForecast'
import { v4 as uuidv4 } from 'uuid';


const ForecastCardExtended = ({surfDataWind, surfDataHoule, onLoad, onLoadMarine, index,functionChange, addActive,isActive}) => {

  const sendBackIndex = () => {
