import React from 'react'
import Day from './minified-components/Day'

const ForecastCardMinified = () => {
    
    const today = new Date();  // Créer un objet Date avec la date et l'heure actuelles
    const options = { weekday: 'long', day: '2-digit' };
    const oneDay = 24 * 60 * 60 * 1000;
    const tomorrow = new Date(today.getTime() + oneDay);  // Obtenir la date de demain
    const afterTomorrow = new Date(today.getTime() + (2 * oneDay));  // Obtenir la date d'après-demain
    const afterAfterTomorrow = new Date(today.getTime() + (3 * oneDay));

    const formattedDate = today.toLocaleDateString('fr-FR', options); // Obtenir la date formatée en utilisant la locale française ('fr-FR')
    const formattedTomorrow = tomorrow.toLocaleDateString('fr-FR', options);
    const formattedAfterTomorrow = afterTomorrow.toLocaleDateString('fr-FR', options);
    const formattedafterAfterTomorrow = afterAfterTomorrow.toLocaleDateString('fr-FR', options);
    console.log(formattedDate);  // Affiche la date du jour au format "DDDD/MM"



  return (
    <div>
        
        <Day />
        <div>Wind</div>
        <div>Energie</div>
        <div>Houle</div>
        <div>Periode</div>
        <div>Tide</div>
    </div>
  )
}

export default ForecastCardMinified
