import React from 'react'

const Day = () => {

    const today = new Date();  // Créer un objet Date avec la date et l'heure actuelles
    const options = { weekday: 'long', year: 'numeric', month: '2-digit', day: '2-digit' };
    const formattedDate = today.toLocaleDateString('fr-FR', options); // Obtenir la date formatée en utilisant la locale française ('fr-FR')
    console.log(formattedDate);  // Affiche la date du jour au format "DDDD/MM"
  return (
    <div>Day</div>
  )
}

export default Day
