import { useState } from 'react';
import logo from '../../../assets/logo-header.svg';
import './NavBar.css';
import RateButton from '../../utilities/RateButton';
import SpotDataBase from '../../utilities/SpotDataBase';

const NavBar = () => {
  const [rating, setRating] = useState(0);
  const [searchInput, setSearchInput] = useState("");
  const [currentSpot, setCurrentSpot] = useState("Your selected spot")

  let spots = SpotDataBase.filter((spot) =>
      spot.name.toLowerCase().match(searchInput.toLowerCase())
  );
  const test = (e) =>{
    setCurrentSpot(SpotDataBase.filter((el) => e.target.innerText === el.name))
  }

  const onSearch =(searchTerm =>{
    // our api to fetch the search result
    console.log('search', searchTerm)
  })

  const onChange = (e) => {
    setSearchInput(e.target.value)
  }

  console.log(currentSpot)
  return (
    <div className="navbar">
      <div className="searchbar">
        <input
          className="search-input"
          type="text"
          placeholder="Select your spot here !"
          value={searchInput}
          onChange= {onChange}
        />
        <button onClick={()=>onSearch}></button>
        <div className='dropdown-search'>
          {searchInput===''? '':spots.map(el=>
            <div onClick={test}>
            {el.name}</div>)}
        </div>
        <div className="current-location">
          <RateButton rating={rating} onRating={(rate) => setRating(rate)} />
          <div className="selected-spot"> {`${currentSpot[0].name}`} </div>
        </div>
        <div className="webcam-link">Access to the webcam</div>
      </div>
      <div className="logo">
        <img src={logo} alt="logo" />
      </div>
      <div className="login">
        <div className="login-align">
          <div className="user-name">User Name</div>
          <div className="user-img"></div>
        </div>
      </div>
    </div>
  );
};

export default NavBar;
