import { useState } from 'react';
import logo from '../../../assets/logo-header.svg';
import './NavBar.css';
import SearchBar from './searchbar/SearchBar';
import Login from "../login/Login";
import Register from "../login/Register";



const NavBar = () => {
    const [currentForm,setCurrentForm]=useState('login')
    
    const toggleForm = (formName) =>{
      setCurrentForm(formName);
    }

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

  return (
    <div className="navbar"> 
      <SearchBar/>
      <div className="logo">
        <img src={logo} alt="logo" />
      </div>
      
      <div className="login">
        <div className="login-align">
          <div className="user-name">Rodrigo La Ride</div>
          <div className="user-img"><img onClick={handleShow} src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTwELv6YPxL_SAptUzg_uxQFI7ambMcaZx1_u-wL0m2jXOVzQA_qtKYziAI6OkPKj086-k&usqp=CAU'/></div>
        {
          currentForm === 'login' ? <Login onFormSwitch={toggleForm} /> : <Register onFormSwitch={toggleForm}/>
        }
        
        </div>
      </div>
    </div>
  );
};


export default NavBar;
